package practice;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10,b=20;
		a=b+a; //a=30
		b=a-b; //b=10
		a=a-b;
		
		System.out.println("the value of a is " +a);
		System.out.println("the value of b is " +b);
		
	}

}
